function WebCam() {
  const [isOpen, setIsOpen] = useState(false);
  const webcamRef = useRef(null);
  return (<>
    <button onClick={()=> setIsOpen(!isOpen)}>Toggle Web Camera</button>
    {isOpen && (<PIPPortal width={400} height={200}>
      <Webcam ref={webcamRef}></Webcam>
    </PIPPortal>)}
  </>);
}

